package question2;

public class Sequence {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	  int i,j;
      for(i=0;i<=6;i++) {
    	  for(j=0;j<=i;j++) {
    		  System.out.print(j*(i+1)+" ");
    
    		  
    	  }
    	  System.out.println(); 
      }
	}

}
